    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if(decline.isSelected()){
            int option=JOptionPane.showConfirmDialog(this, " Do you decline the Codes of Conduct of UoM and thus exit the Registration ?","Confirm your Decision",JOptionPane.YES_NO_CANCEL_OPTION);
            if(option==JOptionPane.YES_OPTION){
                this.dispose();
                Login n=new Login();
                n.setVisible(true);
                n.setLocationRelativeTo(null);
                
            }
        }else{
            JOptionPane.showMessageDialog(this, "Please start the Registration again !");
            Registration r=new Registration();
            r.setVisible(true);
            r.setLocationRelativeTo(null);
            this.dispose();
        }
            
    }//GEN-LAST:event_jButton3ActionPerformed

